# The correct solution is using DP, this is TLE

class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """

        def is_pallindrome(s):
            r = ''.join(reversed(s))
            return r == s

        if len(s) <= 1:
            return s

        locations = {}
        for i, c in enumerate(s):
            locations[c] = locations.get(c, [])
            locations[c].append(i)

        max_substring = ''
        for i, c in enumerate(s):
            available = [_ for _ in locations[c] if _ > i and abs(_ - i) >= len(max_substring)]
            if available:
                for possible in reversed(available):
                    str = s[i:possible + 1]
                    if is_pallindrome(str) and len(str) >= len(max_substring):
                        max_substring = str
                        # print 'Checking {}'.format(possible)
                        break


        return max_substring if max_substring else s[-1]


